import React from 'react';
import PropTypes from 'prop-types';
import classNames from 'classnames';
import { withStyles } from '@material-ui/core/styles';
import MenuItem from '@material-ui/core/MenuItem';
import TextField from '@material-ui/core/TextField';
import Button from '@material-ui/core/Button';
import API from '../API'


import PersonalPageCss from './PersonalPage.css'

const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },
});

class Notes extends React.Component{
  state={
    notes: [],
    note: '',
  }

  componentDidMount(){
    API.getNotes2()
    .then(x=>{
      console.log(x)
      this.setState({
        notes: x
      })
    })
  }

  handleChange = note => event => {
      this.setState({
        [note]: event.target.value,
      });
    };

  handleSubmit = async event => {
    await API.saveNote2(this.state.note)

  }

  deleteNote= (note) =>{
    API.deleteNote2(note)
  }

  render(){
    const { classes } = this.props;
    return(
      <>
        <h1>Notes</h1>
        <ul>
          {this.state.notes!==[]?this.state.notes.map(note=><><li>{note.content}</li><button onClick={()=>this.deleteNote(note)}>delete</button></>):null}
        </ul>
        <form className={classes.container} noValidate autoComplete="off">

    <TextField
    className="NoteField"
          id="outlined-full-width"
          label="Notes"
          style={{ margin: 8 }}
          placeholder="Write your personal Notes."
          onChange={this.handleChange('note')}
          helperText="Keep Track of ICO here!"
          fullWidth
          margin="normal"
          variant="outlined"
          InputLabelProps={{
            shrink: true,
          }}
        />
        </form>
        <Button onClick={()=>{this.handleSubmit()}} variant="contained" color="primary" className={classes.button}>
              Send
        </Button>
        </>

    )
  }


}
Notes.propTypes = {
  classes: PropTypes.object.isRequired,
};

export default withStyles(styles)(Notes);
