import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import { Route, Link, withRouter, Switch } from 'react-router-dom'
import HomePage from './component/HomePage'
import Login from './component/Login'
import Signup from './component/Signup'
import PersonalPage from './component/PersonalPage'



class App extends Component {
  state={
    username: ''
  }
  signin = username => {
    localStorage.setItem('username',username)
    this.setState({
      username: username
    })
  }
  signout = () => {
    localStorage.setItem('',username)
    this.setState({
      username: ''
    })
  }
  // componentDidMount () {
  //   if(!this.state.username){
  //     this.props.history.push('/login')
  //   }
  // }


  render() {
    return (
      <div className="App">
      <Switch>
      <Route exact path='/' component={props=><HomePage username={this.state.username} signout={this.signout} />} />
      <Route exact path='/login' component={props=><Login {...props} signin={this.signin} />} />
      <Route exact path='/signup' component={props=><Signup {...props} signin={this.signin} />} />
      <Route exact path='/personalpage' component={props=><PersonalPage {...props} signout={this.signout} username={this.state.username} />} />
      <Route component={() => <h1>404 - PAGE NOT FOUND!</h1>} />
      </Switch>
      </div>
    );
  }
}

export default withRouter(App)
