import React from 'react'
import { Route, Link, Switch } from 'react-router-dom'
import API from './API'

class Signup extends React.Component{
  state = {
    email: '',
    username: '',
    password: '',
    password_confirmation: ''
  }

  handleSubmit = async event => {
    event.preventDefault()
    if(this.state.password==this.state.password_confirmation){
      API.signup(this.state)
      this.props.history.push('/')}
    else{
      let passwordError=document.getElementById("errors")
      passwordError.innerHTML=`<h5 style="color:red"> Password and Password Confirmation need to be the same </h5>`
    }

  }

  handleChange = event =>{
    this.setState({
      [event.target.name]:event.target.value
    })
  }

  render(){
    return(
      <div>
        <h1>Registration Form </h1>
        <form onSubmit={this.handleSubmit}>
          <label>
            Email:
            <input type="text" name="email" onChange={this.handleChange} />
          </label> <br />
          <label>
            Username:
            <input type="text" name="username" onChange={this.handleChange}/>
          </label> <br />
          <label>
            Password:
            <input type="password" name="password" onChange={this.handleChange}/>
          </label> <br />
          <label id="conf">
            Password Confirmation:
            <input type="password"  name="password_confirmation" onChange={this.handleChange}/>
          </label> <br />
          <input type="submit" value="submit" />
          <div id="errors"></div>
        </form>
        <br />
        <Link to='/'><button>GO BACK</button></Link>
      </div>
    )
  }
}

export default Signup
