import React from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Autocomplete from  'react-autocomplete';
import { getCryptos, matchCryptos } from '../data';



class MenuItems extends React.Component {
  state={
    id: this.props.id,
    coin: '',
    amount: '',
    price_per_unit: ''
  }
  handleChange = async event  => {
    await this.setState({ [event.target.name]: event.target.value });
    this.props.updateCoins(this.state)
  }
  render(){
    return(<>
      <Grid container spacing={24}>
      <Grid item xs={8} sm={3}>

      <InputLabel htmlFor="coin">Coin Type</InputLabel>
      <Autocomplete
          value={ this.state.value }
          name="coin"
          inputProps={{ id: 'states-autocomplete' }}
          wrapperStyle={{ position: 'relative', display: 'inline-block' }}
          items={ getCryptos() }
          getItemValue={ item => item.name }
          onChange={this.handleChange}
          onSelect={this.handleChange}
          renderMenu={ children => (
            <div className = "menu">
              { children }
            </div>
          )}
          renderItem={ (item, isHighlighted) => (
            <div
              className={`item ${isHighlighted ? 'item-highlighted' : ''}`}
              key={ item.abbr } >
              { item.name }
            </div>
          )}
        />
      </Grid>
        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="amount" required id="coinAmount" label="Coin Amount" fullWidth />
        </Grid>

        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="price_per_unit" required id="coinPrice" label="Price per unit" fullWidth />
        </Grid>
      </Grid>

      </>
    )
  }
}
export default MenuItems
