import React from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';



class MenuItems extends React.Component {
  state={
    coin: '',
    amount: 0,
    price_per_unit: 0
  }
  handleChange = event => {
    this.setState({ [event.target.name]: event.target.value });
  }
  render(){
    return(<>
      <Grid container spacing={24}>
      <Grid item xs={8} sm={3}>
      <InputLabel htmlFor="coin">Coin Type</InputLabel>
        <Select
        value={this.state.coin}
          onChange={this.handleChange}
          inputProps={{
            name: 'coin',
            id: 'coin-simple',
          }}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="BTC">BTC</MenuItem>
          <MenuItem value="ETH">ETH</MenuItem>
          <MenuItem value="XRP">XRP</MenuItem>
          <MenuItem value="BTC">BTC</MenuItem>
          <MenuItem value="ETH">ETH</MenuItem>
          <MenuItem value="XRP">XRP</MenuItem>
          <MenuItem value="BTC">BTC</MenuItem>
          <MenuItem value="ETH">ETH</MenuItem>
          <MenuItem value="XRP">XRP</MenuItem>
          <MenuItem value="BTC">BTC</MenuItem>
        </Select>
      </Grid>
        <Grid item xs={8} sm={3}>
          <TextField required id="coinAmount" label="Coin Amount" fullWidth />
        </Grid>

        <Grid item xs={8} sm={3}>
          <TextField required id="coinPrice" label="Price per unit" fullWidth />
        </Grid>
      </Grid>
      <button onClick={()=>{console.log(this.state)}}> butt </button>
      </>
    )
  }
}
export default MenuItems
