import React from 'react'
import { Route, Link, Switch } from 'react-router-dom'
import Button from '@material-ui/core/Button';
import PropTypes from 'prop-types';
import Form from './Form'
import BotParameters from './BotParameters'
import API from '../API'

import { withStyles } from '@material-ui/core/styles';


const styles = theme => ({
  container: {
    display: 'flex',
    flexWrap: 'wrap',
  },
  textField: {
    marginLeft: theme.spacing.unit,
    marginRight: theme.spacing.unit,
  },
  dense: {
    marginTop: 16,
  },
  menu: {
    width: 200,
  },
});

class Bot extends React.Component{
  state={
    information_submitted: false,
    coins_balances:[],
    bot_created: false
  }


  componentDidMount () {
    if(!this.props.username){
      this.props.history.push('/')
    }
    this.getKeys()
    this.getBots()
    }

    getKeys= () =>{
      API.getKeys2()
      .then(x=>{
        console.log(x)
        if(!!x.api_key!==false && !!x.api_secret!==false){
          this.setState({
            information_submitted: true
          },this.getCoinsfromBalances())
        }
      })
    }

    getBots= () =>{
      API.getBots()
      .then(bots=>{
        console.log(bots)
      })
    }

    getCoinsfromBalances = () =>{
      API.getBalances2()
      .then(x=>{

        let coinsArray=x.results.filter(y=>y.Balance!==0)
        this.setState({
          coins_balances: coinsArray
        })
        })
    }


    firstSubmit = () =>{
      this.setState({
        information_submitted:true
      })
    }

    handleSecondSubmit = (bot) =>{
      API.buildBot2(bot)
      .then((x)=>{
        this.setState({
          bot_created: true
        })
      })
    }

    editButton = () =>{
      this.setState({
        information_submitted:false
      })
    }

  render(){
    const { classes } = this.props;

    return(
      <>
        <h1> Create your bot is easy!</h1>
        <p>A Cryptobot is a program that interact with the Bittrex exchange market for you performing automatic actions based on algorithm and strategy that you will decide. At the moment the average daily volatility of bitcoin in the 2 months has been almost 4%. volatility is defined as the difference between the daily maximum and minimum value, these fluctuations can represent a good opportunity for traders even in periods where the daily average values seem to be almost constant. To perform this kind of trading it is required a lot of commitment and attention to follow the markets and perform operations at the right second! CryptoBot can perform these operations for you. Set it up a bot is easy!</p>
        <ul>
          <li>Activate the api key and api secret on Bittrex settings</li>
          <li>allow interaction with 127.0.1 IP in the IP Whitelist</li>
          <li>Submit the informations here, select the coins and strategy </li>
          <li>Done!</li>
        </ul>
        <h2> Instructions </h2>
        <p>Select a coin in your Bittrex wallet, in the "Start Value" field choose the basevalue for your bot to buy the coin, in the Target/Sell Buy choose the value in percentage when you want the bot to sell or buy. The Balance Ratio will calculate how much of that currency balance you want the bot to handle.
        For Example:
        Let's assume you want the bot to buy your XRP coins every time the market goes 2% under the value of 0.25 euro, and sell it again when it increase 2%. Your balance in XPR is 50, and you want just half of this be handled by this strategy.
         The bot will sell the coin when it will reach the start value of 0.25 euro, and buy the same amount as soon as its cost will decrease to 0.245 euro (2%), to sell them again when their value will increase.
         In this case the form should be filled in this way:
         Coin: XRP, Start Value: 0.25,  Buy/Sell Percentage: 2%, Balance Ratio: 0.5</p>
        {this.state.information_submitted?
          (this.state.bot_created?
            <><h2>Bot Created!</h2>
            <button>Stop the bot</button></>:
            <>
          <Button onClick={()=>{this.editButton()}} variant="contained" color="primary" className={classes.button}>
                Edit API info
          </Button>
          <BotParameters handleSecondSubmit={this.handleSecondSubmit} coins={this.state.coins_balances}/>
          </>):
          <Form handleSubmit={this.firstSubmit} handleSecondSubmit={this.handleSecondSubmit} />
}
      </>)
  }
}

Bot.propTypes = {
  classes: PropTypes.object.isRequired,
};
export default withStyles(styles)(Bot)
