import React from 'react'
import { Route, Link, Switch } from 'react-router-dom'
import API from './API'
import MediaCard from './MediaCard'

class PersonalPage extends React.Component{

  state={
    coins: [],
    news: []
  }

  componentDidMount () {
    if(!this.props.username){
      this.props.history.push('/')
    }
     this.stateCoin()
     this.stateNews()
  }

  stateCoin = () =>{
  API.getCoins()
  .then(coins=>this.setState({
    coins: coins
  }))}

  stateNews = () =>{
    API.newsAPI()
    API.getNews()
    .then(news=>{
      console.log(news.results)
      this.setState({
      news: news.results
      })
    })
  }


  render(){
    return(
      <>
        <h1>Welcome {`${this.props.username}`}</h1>
        <div className="grid-2bis">
          <div style={{zIndex:1}} className="col-md-3 col-sm-3 col-xs-6 ">
           <a onClick={()=>{this.props.signout()}} href="#" class="btn btn-sm animated-button victoria-two">Sign Out</a>
          </div>
        </div>
        <div className="content">
          <img src="logo2.png" className="logo" alt="Traversy Media" />
          <div className="title">
          </div>
          <div className="text">
            Keep track of your cryptocurrencies and setup your strategies through bots.
          </div>
        </div>
        <div className="container grid-3 center">

        {this.state.news.slice(0,3).map(singlenews=><div><MediaCard singlenews={singlenews}/></div>)}
        </div>
      </>
    )
  }
}

export default PersonalPage
