import React from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/FormControl';
import Select from 'react-select';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';
import Autocomplete from  'react-autocomplete';
import { getCryptos, matchCryptos } from '../data';

const options= getCryptos()
const customStyles = {

  singleValue: (provided, state) => {
    const opacity = state.isDisabled ? 0.5 : 1;
    const transition = 'opacity 300ms';

    return { ...provided, opacity, transition };
  }
}


class MenuItems extends React.Component {
  state={
    id: this.props.id,
    coin: '',
    amount: '',
    price_per_unit: ''
  }

  componentDidMount() {
    getCryptos()
  }
  handleChange = async event  => {
    await this.setState({ [event.target.name]: event.target.value });
    this.props.updateCoins(this.state)
  }

  handleChangeSelect = async event  => {
    await this.setState({ coin: event.value });
    this.props.updateCoins(this.state)
  }
  render(){
    return(<>
      <Grid container spacing={32}>
      <Grid item xs={8} sm={3}>

      <InputLabel htmlFor="coin">Coin Type</InputLabel>
      <Select
          name="coin"
          value={this.state.coin}
          onChange={this.handleChangeSelect}
          options={options}
          styles={customStyles}

        />
      </Grid>
      <Grid item xs={8} sm={3}>
      <InputLabel htmlFor="coin">Coin Type</InputLabel>

        <TextField value={this.state.coin}  />
      </Grid>
        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="amount" required id="coinAmount" label="Coin Amount" fullWidth />
        </Grid>

        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="price_per_unit" required id="coinPrice" label="Price per unit" fullWidth />
        </Grid>
      </Grid>

      </>
    )
  }
}
export default MenuItems
