import React from 'react';
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import InputLabel from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Grid from '@material-ui/core/Grid';
import TextField from '@material-ui/core/TextField';



class MenuItems extends React.Component {
  state={
    id: this.props.id,
    coin: '',
    amount: '',
    price_per_unit: ''
  }
  handleChange = async event  => {
    await this.setState({ [event.target.name]: event.target.value });
    this.props.updateCoins(this.state)
    console.log(this.state)
  }
  render(){
    return(<>
      <Grid container spacing={24}>
      <Grid item xs={8} sm={3}>
      <InputLabel htmlFor="coin">Coin Type</InputLabel>
        <Select
        value={this.state.coin}
          onChange={this.handleChange}
          inputProps={{
            name: 'coin',
            id: 'coin-simple',
          }}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          <MenuItem value="BTC">BTC</MenuItem>
          <MenuItem value="ETH">ETH</MenuItem>
          <MenuItem value="XRP">XRP</MenuItem>
          <MenuItem value="BCH">BCH</MenuItem>
          <MenuItem value="EOS">EOS</MenuItem>
          <MenuItem value="XLM">XLM</MenuItem>
          <MenuItem value="LTC">LTC</MenuItem>
          <MenuItem value="TRX">TRX</MenuItem>
          <MenuItem value="ADA">ADA</MenuItem>
          <MenuItem value="MIOTA">MIOTA</MenuItem>
        </Select>
      </Grid>
        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="amount" required id="coinAmount" label="Coin Amount" fullWidth />
        </Grid>

        <Grid item xs={8} sm={3}>
          <TextField onChange={this.handleChange} name="price_per_unit" required id="coinPrice" label="Price per unit" fullWidth />
        </Grid>
      </Grid>

      </>
    )
  }
}
export default MenuItems
